 // DEPENDENCIES
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { Capture } from '../../domain/capture-test_db/capture';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../capture.service.ts
 */

/*
 * SCHEMA DB capture
 *
	{
		cType: {
			type: 'Number',
			required : true,
			enum : ["c", "r", "s"]
		},
		captures: {
			type: 'Custom',
			required : true
		},
		filterOutC: {
			type: 'Custom'
		},
		filterOutR: {
			type: 'Custom'
		},
		filterOutS: {
			type: 'Custom'
		},
		url: {
			type: 'String',
			required : true
		},
		//RELATIONS
		//EXTERNAL RELATIONS
	}
 *
 */
@Injectable()
export class CaptureBaseService {

    contextUrl: string = environment.endpoint + '/capture';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * captureService.create
    *   @description CRUD ACTION create
    *   @param Integer cType Object to insert
    *   @param String captures
    *   @param String filterOutC
    *   @param String filterOutR
    *   @param String filterOutS
    *   @param String url
    *
    */
    create(item: Capture): Observable<Capture> {
        return this.http
            .post<Capture>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * captureService.create
    *   @description CRUD ACTION create
    *   @param capture obj Object to insert
    *
    */
    create(item: Capture): Observable<Capture> {
        return this.http
            .post<Capture>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * captureService.delete
    *   @description CRUD ACTION delete
    *   @param ObjectId id Id capture
    *
    */
    remove(id: string): Observable<void> {
        return this.http
            .delete<void>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * captureService.get
    *   @description CRUD ACTION get
    *   @param ObjectId id Id capture
    *   @returns capture
    *
    */
    get(id: string): Observable<Capture> {
        return this.http
            .get<Capture>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * captureService.list
    *   @description CRUD ACTION list
    *   @returns ARRAY OF capture
    *
    */
    list(): Observable<Capture[]> {
        return this.http
            .get<Capture[]>(this.contextUrl)
            .pipe(map(data => data));
    }

    /**
    * captureService.update
    *   @description CRUD ACTION update
    *   @param ObjectId id Id capture
    *   @returns capture
    *
    */
    update(item: Capture): Observable<Capture> {
        return this.http
            .post<Capture>(this.contextUrl + '/' + item._id, item)
            .pipe(map(data => data));
    }


    // Custom APIs

}
